function showFood(){document.getElementById("foodPrice").style.display="block",document.getElementById("drinkPrice").style.display="none"}function showDrink(){document.getElementById("drinkPrice").style.display="block",document.getElementById("foodPrice").style.display="none"}function showSections(sectionIds,targetElementIds){var pageUrl;fetch("/zumlandwirt.html").then(response=>response.text()).then(data=>{var tempContainer=document.createElement("div");tempContainer.innerHTML=data;for(var i=0;i<sectionIds.length;i++){var sectionContent=tempContainer.querySelector("#"+sectionIds[i]),targetElement=document.getElementById(targetElementIds[i]);sectionContent&&targetElement?targetElement.innerHTML=sectionContent.innerHTML:console.error("Section or target element not found in the loaded content.")}}).catch(error=>console.error("Error loading content:",error))}document.addEventListener("DOMContentLoaded",(function(){showSections(["foods","drinks"],["foodPrice","drinkPrice"])}));